    // 動的メモリ確保
    arr = (int *)malloc(size * sizeof(int));
    if (arr == NULL) {
        printf("メモリ確保に失敗しました\n");
        return 1;
    }

    // 配列に値を代入
    for (int i = 0; i < size; i++) {
        arr[i] = i + 1;
    }

    // 結果を出力
    for (int i = 0; i < size; i++) {
        printf("arr[%d] = %d\n", i, arr[i]);
    }

    // メモリ解放
    free(arr);

    return 0;
}

