#include <stdio.h>

int factorial(int n) {
    if (n == 0) return 1;
    return n * factorial(n - 1);
}

int main(void) {
    int num = 5;

    // 再帰関数を使用して階乗を計算
    printf("%dの階乗は: %d\n", num, factorial(num));

    return 0;
}

