    for (i = 2; i <= 50; i++) {
        is_prime = 1;  // 素数かどうかを判定するフラグ
        for (j = 2; j <= i / 2; j++) {
            if (i % j == 0) {
                is_prime = 0;  // 素数でない場合
                break;
            }
        }
        if (is_prime == 1) {
            printf("%dは素数です。\n", i);
        }
    }

    return 0;
}

