#include <stdio.h>
#include <string.h>

int main(void) {
    char str[5][100];
    char temp[100];
    int n = 5;

    printf("5つの文字列を入力してください:\n");
    for (int i = 0; i < n; i++) {
        fgets(str[i], sizeof(str[i]), stdin);
        str[i][strcspn(str[i], "\n")] = '\0';  // 改行文字を取り除く
    }

    // 辞書順に並べ替え
    for (int i = 0; i < n - 1; i++) {
        for (int j = i + 1; j < n; j++) {
            if (strcmp(str[i], str[j]) > 0) {
                strcpy(temp, str[i]);
                strcpy(str[i], str[j]);
                strcpy(str[j], temp);
            }
        }
    }

    printf("辞書順に並べ替えた文字列:\n");
    for (int i = 0; i < n; i++) {
        printf("%s\n", str[i]);
    }

    return 0;
}

#include <stdio.h>
#include <string.h>

int main(void) {
    char str[100];
    int freq[256] = {0};  // ASCIIの文字数分
    char mostFreqChar;
    int maxCount = 0;

    printf("文字列を入力してください: ");
    fgets(str, sizeof(str), stdin);
    str[strcspn(str, "\n")] = '\0';  // 改行文字を取り除く

    for (int i = 0; str[i] != '\0'; i++) {
        freq[(int)str[i]]++;  // 文字ごとの出現回数をカウント
    }

    for (int i = 0; i < 256; i++) {
        if (freq[i] > maxCount) {
            maxCount = freq[i];
            mostFreqChar = (char)i;
        }
    }

    printf("最も頻繁に出現する文字: '%c' 出現回数: %d\n", mostFreqChar, maxCount);

    return 0;
}

#include <stdio.h>
