int main(void) {
    char str[100];
    int len, isPalindrome = 1;

    printf("文字列を入力してください: ");
    fgets(str, sizeof(str), stdin);
    str[strcspn(str, "\n")] = '\0';  // 改行文字を取り除く

    len = strlen(str);

    // 回文かどうかをチェック
    for (int i = 0; i < len / 2; i++) {
        if (str[i] != str[len - i - 1]) {
            isPalindrome = 0;
            break;
        }
    }

    if (isPalindrome) {
        printf("この文字列は回文です。\n");
    } else {
        printf("この文字列は回文ではありません。\n");
    }

    return 0;
}

