#include <stdio.h>
#include <string.h>

int main(void) {
    char str[100];

    printf("文字列を入力してください: ");
    fgets(str, sizeof(str), stdin);
    str[strcspn(str, "\n")] = '\0';  // 改行文字を取り除く

    for (int i = 0; str[i] != '\0'; i++) {
        if (str[i] >= 'a' && str[i] <= 'z') {
            str[i] = 'z' - (str[i] - 'a');  // aはzに、bはyに置き換える
        } else if (str[i] >= 'A' && str[i] <= 'Z') {
            str[i] = 'Z' - (str[i] - 'A');  // AはZに、BはYに置き換える
        }
    }

    printf("置き換え後の文字列: %s\n", str);

    return 0;
}

