#include <stdio.h>
#include <stdlib.h>

void addItem(int[], int*, int);  // Add item function
void removeItem(int[], int*, int);  // Remove item function
int searchItem(int[], int, int);  // Search item function
void displayList(int[], int);  // Display list function

int main(void) {
    int list[10];  // List array
    int size = 0;  // Current list size
    int choice, value;

    while (1) {
        printf("1. Add item 2. Remove item 3. Search item 4. Display list 5. Exit\n");
        printf("Enter your choice: ");
        scanf("%d", &choice);

        switch (choice) {
            case 1:
                printf("Enter value to add: ");
                scanf("%d", &value);
                addItem(list, &size, value);
                break;
            case 2:
                printf("Enter value to remove: ");
                scanf("%d", &value);
                removeItem(list, &size, value);
                break;
            case 3:
                printf("Enter value to search: ");
                scanf("%d", &value);
                if (searchItem(list, size, value) != -1) {
                    printf("Value %d exists in the list.\n", value);
                } else {
                    printf("Value %d does not exist in the list.\n", value);
                }
                break;
            case 4:
                displayList(list, size);
                break;
            case 5:
                printf("Exiting program.\n");
                return 0;
            default:
                printf("Invalid choice.\n");
        }
    }
}

void addItem(int list[], int* size, int value) {
    if (*size < 10) {
        list[*size] = value;
        (*size)++;
        printf("Value %d added to the list.\n", value);
    } else {
        printf("List is full.\n");
    }
}

void removeItem(int list[], int* size, int value) {
    int i, found = 0;
    for (i = 0; i < *size; i++) {
        if (list[i] == value) {
            found = 1;
            break;
        }
    }
    if (found) {
        for (int j = i; j < *size - 1; j++) {
            list[j] = list[j + 1];
        }
        (*size)--;
        printf("Value %d removed from the list.\n", value);
    } else {
        printf("Value %d not found in the list.\n", value);
    }
}

int searchItem(int list[], int size, int value) {
    for (int i = 0; i < size; i++) {
        if (list[i] == value) {
            return i;
        }
    }
    return -1;
}

void displayList(int list[], int size) {
    if (size == 0) {
        printf("The list is empty.\n");
    } else {
        printf("List contents:\n");
        for (int i = 0; i < size; i++) {
            printf("%d ", list[i]);
        }
        printf("\n");
    }
}

