#include <stdio.h>
#include <stdlib.h>

typedef struct Stack {
    int data;
    struct Stack *next;
} Stack;

Stack *push(Stack *top, int data) {
    Stack *newNode = (Stack *)malloc(sizeof(Stack));
    newNode->data = data;
    newNode->next = top;
    return newNode;
}

Stack *pop(Stack *top, int *data) {
    if (top == NULL) {
        printf("Stack is empty.\n");
        return NULL;
    }
    *data = top->data;
    Stack *temp = top;
    top = top->next;
    free(temp);
    return top;
}

void printStack(Stack *top) {
    while (top != NULL) {
        printf("%d -> ", top->data);
        top = top->next;
    }
    printf("NULL\n");
}

int main(void) {
    Stack *stack = NULL;
    int poppedData;

    stack = push(stack, 10);
    stack = push(stack, 20);
    stack = push(stack, 30);

    printf("Stack: ");
    printStack(stack);

    stack = pop(stack, &poppedData);
    printf("Popped: %d\n", poppedData);

    printf("Stack after pop: ");
    printStack(stack);

    return 0;
}

#include <stdio.h>
#include <stdlib.h>

int main(void) {
    int r1, c1, r2, c2;
    printf("Enter dimensions of matrix 1 (rows columns): ");
    scanf("%d %d", &r1, &c1);
    printf("Enter dimensions of matrix 2 (rows columns): ");
    scanf("%d %d", &r2, &c2);

    if (c1 != r2) {
        printf("Matrix multiplication not possible.\n");
        return 1;
    }

    int **mat1 = malloc(r1 * sizeof(int *));
    int **mat2 = malloc(r2 * sizeof(int *));
    int **result = malloc(r1 * sizeof(int *));

    for (int i = 0; i < r1; i++) mat1[i] = malloc(c1 * sizeof(int));
    for (int i = 0; i < r2; i++) mat2[i] = malloc(c2 * sizeof(int));
    for (int i = 0; i < r1; i++) result[i] = malloc(c2 * sizeof(int));

    printf("Enter elements of matrix 1:\n");
    for (int i = 0; i < r1; i++)
        for (int j = 0; j < c1; j++)
            scanf("%d", &mat1[i][j]);

    printf("Enter elements of matrix 2:\n");
    for (int i = 0; i < r2; i++)
        for (int j = 0; j < c2; j++)
            scanf("%d", &mat2[i][j]);

    for (int i = 0; i < r1; i++) {
        for (int j = 0; j < c2; j++) {
            result[i][j] = 0;
            for (int k = 0; k < c1; k++)
                result[i][j] += mat1[i][k] * mat2[k][j];
        }
    }

    printf("Resultant matrix:\n");
    for (int i = 0; i < r1; i++) {
        for (int j = 0; j < c2; j++)
            printf("%d ", result[i][j]);
        printf("\n");
    }

    for (int i = 0; i < r1; i++) free(mat1[i]);
    for (int i = 0; i < r2; i++) free(mat2[i]);
    for (int i = 0; i < r1; i++) free(result[i]);
    free(mat1);
    free(mat2);
    free(result);

    return 0;
}

