#include <stdio.h>
#include <string.h>

int main() {
    FILE *file = fopen("data.csv", "r");
    if (file == NULL) {
        printf("Error opening file\n");
        return 1;
    }
    char line[1024];
    while (fgets(line, sizeof(line), file)) {
        char *token = strtok(line, ",");
        while (token != NULL) {
            printf("%s	", token);
            token = strtok(NULL, ",");
        }
        printf("\n");
    }
    fclose(file);
    return 0;
}

