#include <stdio.h>
#include <string.h>

int main() {
    char line[256], searchString[] = "Hello";
    int lineNumber = 0, found = 0;
    FILE *file = fopen("file.txt", "r");
    if (file == NULL) {
        printf("Error opening file\n");
        return 1;
    }
    while (fgets(line, sizeof(line), file)) {
        lineNumber++;
        if (strstr(line, searchString)) {
            printf("Found '%s' at line %d\n", searchString, lineNumber);
            found = 1;
            break;
        }
    }
    fclose(file);
    if (!found) {
        printf("'%s' not found in the file.\n", searchString);
    }
    return 0;
}

#include <stdio.h>
#include <string.h>

int main() {
    char word[50], line[256];
    int count = 0;
    FILE *file = fopen("file.txt", "r");
    if (file == NULL) {
        printf("Error opening file\n");
        return 1;
    }
    printf("Enter the word to search for: ");
    scanf("%s", word);
    while (fgets(line, sizeof(line), file)) {
        if (strstr(line, word)) {
            count++;
        }
    }
    fclose(file);
    printf("The word '%s' appears %d times in the file.\n", word, count);
    return 0;
}
