#include <stdio.h>
#include <math.h>

void input(double *a, double *b, double *c);
void calc(double a, double b, double c, double *root1_real, double *root2_real, double *root_imaginary, int *root_type);
void output(double root1_real, double root2_real, double root_imaginary, int root_type);

int main() {
    double a, b, c;
    double root1_real, root2_real;
    double root_imaginary = 0;
    int root_type;

    input(&a, &b, &c);

    calc(a, b, c, &root1_real, &root2_real, &root_imaginary, &root_type);
    output(root1_real, root2_real, root_imaginary, root_type);

    return 0;
}

void input(double *a, double *b, double *c) {
    printf("Enter the coefficient on the quadratic equation ax squared plus bx plus c is equal to 0.\n");
    printf("a (coefficient of x^2): ");
    scanf("%lf", a);
    printf("b (coefficient of x): ");
    scanf("%lf", b);
    printf("c (Constant terms): ");
    scanf("%lf", c);
}

void calc(double a, double b, double c, double *root1_real, double *root2_real, double *root_imaginary, int *root_type) {
    double discriminant = b * b - 4 * a * c;

    if (discriminant > 0) {
        *root_type = 0;
        *root1_real = (-b + sqrt(discriminant)) / (2 * a);
        *root2_real = (-b - sqrt(discriminant)) / (2 * a);
    } else if (discriminant == 0) {
        *root_type = 1;
        *root1_real = *root2_real = -b / (2 * a);
    } else {
        *root_type = 2;
        *root1_real = *root2_real = -b / (2 * a);
        *root_imaginary = sqrt(-discriminant) / (2 * a);
    }
}

void output(double root1_real, double root2_real, double root_imaginary, int root_type) {
    if (root_type == 0) {
        printf("Two different real solutions: \n");
        printf("x1 = %.2f\n", root1_real);
        printf("x2 = %.2f\n", root2_real);
    } else if (root_type == 1) {
        printf("Multi-solution of real numbers: \n");
        printf("x = %.2f\n", root1_real);
    } else if (root_type == 2) {
        printf("One set of conjugated imaginary solutions: \n");
        printf("x1 = %.2f + %.2fi\n", root1_real, root_imaginary);
        printf("x2 = %.2f - %.2fi\n", root2_real, root_imaginary);
    }
}

