            case 1:
                printf("Enter contact name: ");
                getchar(); // To consume newline character
                fgets(name, sizeof(name), stdin);
                printf("Enter contact phone number: ");
                fgets(phoneNumber, sizeof(phoneNumber), stdin);
                printf("Enter contact email: ");
                fgets(email, sizeof(email), stdin);
                addContact(contacts, &contactCount, name, phoneNumber, email);
                break;
            case 2:
                displayContacts(contacts, contactCount);
                break;
            case 3:
                printf("Exiting program.\n");
                return 0;
            default:
                printf("Invalid choice!\n");
        }
    }

    return 0;
}

void addContact(struct Contact *contacts, int *count, const char *name, const char *phoneNumber, const char *email) {
    snprintf(contacts[*count].name, sizeof(contacts[*count].name), "%s", name);
    snprintf(contacts[*count].phoneNumber, sizeof(contacts[*count].phoneNumber), "%s", phoneNumber);
    snprintf(contacts[*count].email, sizeof(contacts[*count].email), "%s", email);
    (*count)++;
}

void displayContacts(struct Contact contacts[], int count) {
    if (count == 0) {
        printf("No contacts available!\n");
    } else {
        for (int i = 0; i < count; i++) {
            printf("\nContact %d:\n", i + 1);
            printf("Name: %s", contacts[i].name);
            printf("Phone Number: %s", contacts[i].phoneNumber);
            printf("Email: %s\n", contacts[i].email);
        }
    }
}

