    printf("Enter a number: ");
    scanf("%d", &num);

    if (num < 0) {
        printf("Error: Factorial of a negative number is undefined.\n");
        return 1;
    }

    printf("Factorial of %d is %lld\n", num, factorial(num));
    return 0;
}

long long factorial(int n) {
    if (n == 0 || n == 1) {
        return 1;
    }
    return n * factorial(n - 1);
}

