}

int findMax(int arr[], int size) {
    int max = arr[0];
    for (int i = 1; i < size; i++) {
        if (arr[i] > max) {
            max = arr[i];
        }
    }
    return max;
}

#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <time.h>
#include <termios.h>
#include <unistd.h>

#define WIDTH 15
#define HEIGHT 10
#define STARTX (WIDTH / 2)
#define STARTY (HEIGHT / 2)
#define MAXLENGTH ((WIDTH - 2) * (HEIGHT - 2))
#define LENGTH_LEV1 (MAXLENGTH / 3)
#define LENGTH_LEV2 (LENGTH_LEV1 * 2)
#define TIME_LEV1 50
#define TIME_LEV2 30
#define TIME_LEV3 10
#define LOOPNUM 10

enum direction {
    left,
    up,
    right,
    down,
    non
};

typedef struct snakePosition {
    int x, y;
    enum direction nextDirection;
} snake;

typedef struct snakeFood {
    int x, y;
} food;

int field[HEIGHT][WIDTH];
snake snake1[MAXLENGTH];
food food1;
int snakeLength = 2;
int eatFlag = 0;

int init();
int viewField();
int countSec();
enum direction getKey();
int shiftSnake();
int moveSnake(enum direction key);
int makeFood();
int judge();
void setTerminalMode();
void resetTerminalMode();
char getch();

int main() {
    enum direction key;
    int loopCount = 0;

    init();
    viewField();

    while (1) {
        countSec();
        key = getKey();
        if (loopCount == LOOPNUM || key != non) {
            moveSnake(key);
            if (judge() < 0) break;
            viewField();
            loopCount = 0;
        }
        loopCount += 1;
    }

    resetTerminalMode();
    return 0;
}

int init() {
    for (int i = 0; i < HEIGHT; i++) {
        for (int j = 0; j < WIDTH; j++) {
            if (i == 0 || i == (HEIGHT - 1) || j == 0 || j == (WIDTH - 1)) field[i][j] = 1;
            else field[i][j] = 0;
        }
    }

    snakeLength = 2;
    memset(snake1, 0, sizeof(snake1));
    snake1[0].x = STARTX;
    snake1[0].y = STARTY;
    snake1[0].nextDirection = left;

    snake1[1].x = STARTX + 1;
    snake1[1].y = STARTY;

    makeFood();

    return 0;
}

int viewField() {
    system("clear");

    for (int i = 0; i < HEIGHT; i++) {
        for (int j = 0; j < WIDTH; j++) {
            if (i == 0 || i == (HEIGHT - 1) || j == 0 || j == (WIDTH - 1)) field[i][j] = 1;
            else field[i][j] = 0;
        }
    }

    for (int i = 0; i < snakeLength; i++) {
        field[(HEIGHT - 1) - snake1[i].y][snake1[i].x] = 2;
    }

    field[(HEIGHT - 1) - food1.y][food1.x] = 3;

    for (int i = 0; i < HEIGHT; i++) {
        for (int j = 0; j < WIDTH; j++) {
            if (field[i][j] == 1) printf("");
            else if (field[i][j] == 2) printf("");
            else if (field[i][j] == 3) printf("Food");
            else printf(" ");
            if (j == (WIDTH - 1)) printf("\n");
        }
    }

    return 0;
}

int countSec() {
    double startTime = 0.0;
    double endTime = 0.0;
    double totalTime = 0.0;

    int level = 0;

    if (snakeLength < LENGTH_LEV1) level = TIME_LEV1;
    else if (snakeLength >= LENGTH_LEV1 && snakeLength < LENGTH_LEV2) level = TIME_LEV2;
    else level = TIME_LEV3;

    startTime = clock();
    while (1) {
        endTime = clock();
        totalTime = endTime - startTime;
        if (totalTime > TIME_LEV1) break;
    }

    return 0;
}

enum direction getKey() {
    char key = getch();

    if (key == 'a') return left;
    else if (key == 'w') return up;
    else if (key == 'd') return right;
    else if (key == 's') return down;
    else return non;
}

int shiftSnake() {
    if (snakeLength == 1) return -1;

    for (int i = 1; i < snakeLength; i++) {
        if (snake1[snakeLength - i].x == snake1[snakeLength - (i + 1)].x
            && snake1[snakeLength - i].y == snake1[snakeLength - (i + 1)].y) {
            continue;
        }

        snake1[snakeLength - i].x = snake1[snakeLength - (i + 1)].x;
        snake1[snakeLength - i].y = snake1[snakeLength - (i + 1)].y;
    }

    return 0;
}

int moveSnake(enum direction key) {
    switch (key) {
    case left:
        if (snake1[0].x == (snake1[1].x + 1)) {
            shiftSnake();
            snake1[0].nextDirection = key;
            break;
        }
        break;
    case up:
        if (snake1[0].y == (snake1[1].y - 1)) {
            shiftSnake();
            snake1[0].nextDirection = key;
            break;
        }
        break;
    case right:
        if (snake1[0].x == (snake1[1].x - 1)) {
            shiftSnake();
            snake1[0].nextDirection = key;
            break;
        }
        break;
    case down:
        if (snake1[0].y == (snake1[1].y + 1)) {
            shiftSnake();
            snake1[0].nextDirection = key;
            break;
        }
        break;
    }

    return 0;
}

int makeFood() {
    food1.x = rand() % (WIDTH - 2) + 1;
    food1.y = rand() % (HEIGHT - 2) + 1;
    return 0;
}

int judge() {
    if (snake1[0].x == food1.x && snake1[0].y == food1.y) {
        eatFlag = 1;
        makeFood();
    }

    return 0;
}

void setTerminalMode() {
    struct termios newt;
    tcgetattr(STDIN_FILENO, &newt);
    newt.c_lflag &= ~ICANON;   // Disable canonical mode
    newt.c_lflag &= ~ECHO;     // Disable echoing
    newt.c_cc[VMIN] = 1;       // Minimum number of characters to read
    newt.c_cc[VTIME] = 0;      // Timeout value
    tcsetattr(STDIN_FILENO, TCSANOW, &newt);
}

void resetTerminalMode() {
    struct termios oldt;
    tcgetattr(STDIN_FILENO, &oldt);
    oldt.c_lflag |= ICANON;    // Enable canonical mode
    oldt.c_lflag |= ECHO;      // Enable echoing
    tcsetattr(STDIN_FILENO, TCSANOW, &oldt);
}

char getch() {
    char buf = 0;
    setTerminalMode();
    read(STDIN_FILENO, &buf, 1);
    resetTerminalMode();
    return buf;
}

