#include <stdio.h>
#include <stdlib.h>

char board[3][3];

void printBoard() {
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            printf("%c ", board[i][j]);
        }
        printf("\n");
    }
}

int checkWinner() {
    for (int i = 0; i < 3; i++) {
        if (board[i][0] == board[i][1] && board[i][1] == board[i][2] && board[i][0] != ' ') return 1;
        if (board[0][i] == board[1][i] && board[1][i] == board[2][i] && board[0][i] != ' ') return 1;
    }
    if (board[0][0] == board[1][1] && board[1][1] == board[2][2] && board[0][0] != ' ') return 1;
    if (board[0][2] == board[1][1] && board[1][1] == board[2][0] && board[0][2] != ' ') return 1;
    return 0;
}

int main() {
    int row, col;
    int player = 1;
    int moveCount = 0;

    // Initialize the board
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            board[i][j] = ' ';
        }
    }

    while (1) {
        printBoard();
        printf("Player %d, enter row and column: ", player);
        scanf("%d %d", &row, &col);

        if (board[row][col] == ' ') {
            board[row][col] = (player == 1) ? 'X' : 'O';
            moveCount++;
            if (checkWinner()) {
                printBoard();
                printf("Player %d wins!\n", player);
                break;
            }
            if (moveCount == 9) {
                printBoard();
                printf("It's a draw!\n");
                break;
            }
            player = 3 - player; // Switch player
        } else {
            printf("Cell already taken, try again.\n");
        }
    }
    return 0;
}

