#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_ATTEMPTS 6

void displayWord(char word[], char guessed[]) {
    for (int i = 0; i < strlen(word); i++) {
        if (guessed[i] == 1) {
            printf("%c ", word[i]);
        } else {
            printf("_ ");
        }
    }
    printf("\n");
}

int main() {
    char word[] = "programming";
    char guessed[strlen(word)];
    int attempts = 0;
    int correctGuess = 0;
    char guess;

    for (int i = 0; i < strlen(word); i++) {
        guessed[i] = 0;
    }

    printf("Welcome to Hangman!\n");

    while (attempts < MAX_ATTEMPTS && correctGuess < strlen(word)) {
        displayWord(word, guessed);
        printf("Enter a guess: ");
        scanf(" %c", &guess);

        int found = 0;
        for (int i = 0; i < strlen(word); i++) {
            if (word[i] == guess && !guessed[i]) {
                guessed[i] = 1;
                correctGuess++;
                found = 1;
            }
        }

        if (!found) {
            attempts++;
            printf("Wrong guess. You have %d attempts left.\n", MAX_ATTEMPTS - attempts);
        }

        if (correctGuess == strlen(word)) {
            printf("Congratulations! You've guessed the word: %s\n", word);
            break;
        }
    }

    if (attempts == MAX_ATTEMPTS) {
        printf("You've lost. The word was: %s\n", word);
    }

    return 0;
}

