        scanf("%d", &guess[i]);
    }

    // Check if the guess is correct
    for (int i = 0; i < SEQUENCE_LENGTH; i++) {
        if (guess[i] == sequence[i]) {
            correct++;
        }
    }

    if (correct == SEQUENCE_LENGTH) {
        printf("You guessed correctly!\n");
    } else {
        printf("You guessed %d out of %d correctly.\n", correct, SEQUENCE_LENGTH);
    }

    return 0;
}

