struct Node {
    int data;
    struct Node *next;
};

int main() {
    int n = 3;
    struct Node *head = NULL;
    struct Node *temp;

    for (int i = 0; i < n; i++) {
        struct Node *newNode = (struct Node *)malloc(sizeof(struct Node));
        if (newNode == NULL) {
            printf("Memory allocation failed!\n");
            return 1;
        }
        newNode->data = i + 1;
        newNode->next = NULL;

        if (head == NULL) {
            head = newNode;
        } else {
            temp->next = newNode;
        }
        temp = newNode;
    }

    temp = head;
    while (temp != NULL) {
        printf("%d -> ", temp->data);
        temp = temp->next;
    }
    printf("NULL\n");

    temp = head;
    while (temp != NULL) {
        struct Node *toFree = temp;
        temp = temp->next;
        free(toFree);
    }

    return 0;
}

