union Number {
    int decimal;
    char binary[32];
};

void intToBinary(int n, char *binary) {
    for (int i = 31; i >= 0; i--) {
        binary[i] = (n % 2) + '0';
        n /= 2;
    }
    binary[32] = '\0';
}

int main() {
    union Number num;
    num.decimal = 10;
    printf("Decimal: %d\n", num.decimal);
    intToBinary(num.decimal, num.binary);
    printf("Binary: %s\n", num.binary);

    return 0;
}

