#include <stdio.h>
#include <math.h>

union Shape {
    struct {
        float length, width;
    } rectangle;
    struct {
        float radius;
    } circle;
};

float calculateArea(union Shape shape, char type) {
    if (type == 'r') {
        return shape.rectangle.length * shape.rectangle.width;
    } else if (type == 'c') {
        return M_PI * shape.circle.radius * shape.circle.radius;
    }
    return 0.0;
}

int main() {
    union Shape shape;
    shape.rectangle.length = 10.0;
    shape.rectangle.width = 5.0;
    printf("Area of rectangle: %.2f\n", calculateArea(shape, 'r'));

    shape.circle.radius = 7.0;
    printf("Area of circle: %.2f\n", calculateArea(shape, 'c'));

    return 0;
}

#include <stdio.h>
