union NameHash {
    char name[50];
    int hash;
};

int generateHash(const char *str) {
    int hash = 0;
    while (*str) {
        hash = (hash << 5) + *str++;
    }
    return hash;
}

int main() {
    union NameHash nh;
    strcpy(nh.name, "Alice");
    printf("Name: %s\n", nh.name);
    nh.hash = generateHash(nh.name);
    printf("Hash value: %d\n", nh.hash);

    return 0;
}

