#include <stdio.h>
#include <stdlib.h>
#include <conio.h>
#include <windows.h>

#define WIDTH 20
#define HEIGHT 20
#define MAX_LENGTH 100

int x, y, foodX, foodY, score;
int tailX[MAX_LENGTH], tailY[MAX_LENGTH];
int nTail;
int gameOver;

void setup() {
    gameOver = 0;
    x = WIDTH / 2;
    y = HEIGHT / 2;
    foodX = rand() % WIDTH;
    foodY = rand() % HEIGHT;
    score = 0;
}

void draw() {
    system("cls");
    for (int i = 0; i < WIDTH + 2; i++)
        printf("#");
    printf("\n");
    for (int i = 0; i < HEIGHT; i++) {
        for (int j = 0; j < WIDTH; j++) {
            if (j == 0)
                printf("#");
            else if (j == x && i == y)
                printf("O");
            else if (j == foodX && i == foodY)
                printf("*");
            else {
                int isPrint = 0;
                for (int k = 0; k < nTail; k++) {
                    if (tailX[k] == j && tailY[k] == i) {
                        printf("o");
                        isPrint = 1;
                    }
                }
                if (!isPrint) printf(" ");
            }
            if (j == WIDTH - 1) printf("#");
        }
        printf("\n");
    }
    for (int i = 0; i < WIDTH + 2; i++)
        printf("#");
    printf("\n");
    printf("Score: %d\n", score);
}

void input() {
    if (_kbhit()) {
        switch (_getch()) {
            case 'a': x--; break;
            case 'd': x++; break;
            case 'w': y--; break;
            case 's': y++; break;
            case 'x': gameOver = 1; break;
        }
    }
}

void logic() {
    int prevX = tailX[0];
    int prevY = tailY[0];
    int prev2X, prev2Y;
    tailX[0] = x;
    tailY[0] = y;
    for (int i = 1; i < nTail; i++) {
        prev2X = tailX[i];
        prev2Y = tailY[i];
        tailX[i] = prevX;
        tailY[i] = prevY;
        prevX = prev2X;
        prevY = prev2Y;
    }
    if (x == foodX && y == foodY) {
        score += 10;
        foodX = rand() % WIDTH;
        foodY = rand() % HEIGHT;
        nTail++;
    }
    if (x < 0 || x >= WIDTH || y < 0 || y >= HEIGHT)
        gameOver = 1;
    for (int i = 0; i < nTail; i++) {
        if (tailX[i] == x && tailY[i] == y)
            gameOver = 1;
    }
}

void systemSleep() {
    Sleep(100);
}

int main() {
    setup();
    while (!gameOver) {
        draw();
        input();
        logic();
        systemSleep();
    }
    printf("Game Over!\n");
    return 0;
}

#include <stdio.h>
#include <stdlib.h>
#include <conio.h>

#define WIDTH 10
#define HEIGHT 20

int field[HEIGHT][WIDTH] = {0};
int currentPiece[4][2], currentPieceType;

void setup() {
    for (int i = 0; i < 4; i++) {
        for (int j = 0; j < 2; j++) {
            currentPiece[i][j] = 0;
        }
    }
}

void draw() {
    system("cls");
    for (int y = 0; y < HEIGHT; y++) {
        for (int x = 0; x < WIDTH; x++) {
            if (field[y][x])
                printf("#");
            else
                printf(".");
        }
        printf("\n");
    }
}

void rotatePiece() {
    int temp[4][2];
    for (int i = 0; i < 4; i++) {
        temp[i][0] = -currentPiece[i][1];
        temp[i][1] = currentPiece[i][0];
    }
    for (int i = 0; i < 4; i++) {
        currentPiece[i][0] = temp[i][0];
        currentPiece[i][1] = temp[i][1];
    }
}

void movePiece(int dx, int dy) {
    for (int i = 0; i < 4; i++) {
        currentPiece[i][0] += dx;
        currentPiece[i][1] += dy;
    }
}

int checkCollision() {
    for (int i = 0; i < 4; i++) {
        if (currentPiece[i][0] < 0 || currentPiece[i][0] >= WIDTH ||
            currentPiece[i][1] < 0 || currentPiece[i][1] >= HEIGHT)
            return 1;
        if (field[currentPiece[i][1]][currentPiece[i][0]])
            return 1;
    }
    return 0;
}

void dropPiece() {
    for (int i = 0; i < 4; i++) {
        field[currentPiece[i][1]][currentPiece[i][0]] = 1;
    }
}

int main() {
    setup();
    while (1) {
        draw();
        if (_kbhit()) {
            char ch = _getch();
            if (ch == 'a') movePiece(-1, 0);
            if (ch == 'd') movePiece(1, 0);
            if (ch == 'w') rotatePiece();
        }
        movePiece(0, 1);
        if (checkCollision()) {
            dropPiece();
            setup();
        }
        Sleep(100);
    }
    return 0;
}

#include <stdio.h>
#include <stdlib.h>
#include <conio.h>
#include <windows.h>

#define WIDTH 20
#define HEIGHT 10
#define MAX_ENEMIES 5

int playerX = WIDTH / 2, playerY = HEIGHT - 1;
int bulletX = -1, bulletY = -1;
int enemies[MAX_ENEMIES][2];
int score = 0;

void setup() {
    for (int i = 0; i < MAX_ENEMIES; i++) {
        enemies[i][0] = rand() % WIDTH;
        enemies[i][1] = rand() % (HEIGHT / 2);
    }
}

void draw() {
    system("cls");
    for (int y = 0; y < HEIGHT; y++) {
        for (int x = 0; x < WIDTH; x++) {
            if (x == playerX && y == playerY) printf("A");
            else if (x == bulletX && y == bulletY) printf("|");
            else {
                int isEnemy = 0;
                for (int i = 0; i < MAX_ENEMIES; i++) {
                    if (enemies[i][0] == x && enemies[i][1] == y) {
                        printf("E");
                        isEnemy = 1;
                    }
                }
                if (!isEnemy) printf(" ");
            }
        }
        printf("\n");
    }
    printf("Score: %d\n", score);
}

void input() {
    if (_kbhit()) {
        switch (_getch()) {
            case 'a': if (playerX > 0) playerX--; break;
            case 'd': if (playerX < WIDTH - 1) playerX++; break;
            case 'w': if (bulletY == -1) {
                bulletX = playerX;
                bulletY = playerY - 1;
            } break;
        }
    }
}

void logic() {
    if (bulletY > -1) bulletY--;
    if (bulletY < 0) bulletX = -1;
    for (int i = 0; i < MAX_ENEMIES; i++) {
        if (enemies[i][0] == bulletX && enemies[i][1] == bulletY) {
            enemies[i][0] = rand() % WIDTH;
            enemies[i][1] = rand() % (HEIGHT / 2);
            score += 10;
            bulletY = -1;
            break;
        }
    }
    for (int i = 0; i < MAX_ENEMIES; i++) {
        if (enemies[i][1] == HEIGHT - 1) {
            printf("Game Over!\n");
            exit(0);
        }
        enemies[i][1]++;
    }
}

int main() {
    setup();
    while (1) {
        draw();
        input();
        logic();
        Sleep(100);
    }
    return 0;
}

#include <stdio.h>
#include <stdlib.h>
#include <conio.h>
#include <windows.h>

#define WIDTH 30
#define HEIGHT 10

int ballX, ballY, ballDX, ballDY, paddleX, paddleWidth;

void setup() {
    ballX = WIDTH / 2;
    ballY = HEIGHT / 2;
    ballDX = 1;
    ballDY = 1;
    paddleX = WIDTH / 2 - 2;
    paddleWidth = 5;
}

void draw() {
    system("cls");
    for (int y = 0; y < HEIGHT; y++) {
        for (int x = 0; x < WIDTH; x++) {
            if (y == ballY && x == ballX) printf("O");
            else if (y == HEIGHT - 1 && x >= paddleX && x < paddleX + paddleWidth) printf("#");
            else printf(" ");
        }
        printf("\n");
    }
}

void input() {
    if (_kbhit()) {
        switch (_getch()) {
            case 'a': if (paddleX > 0) paddleX--; break;
            case 'd': if (paddleX + paddleWidth < WIDTH) paddleX++; break;
        }
    }
}

void logic() {
    ballX += ballDX;
    ballY += ballDY;
    if (ballX == 0 || ballX == WIDTH - 1) ballDX = -ballDX;
    if (ballY == 0) ballDY = -ballDY;
    if (ballY == HEIGHT - 1 && ballX >= paddleX && ballX < paddleX + paddleWidth) ballDY = -ballDY;
    if (ballY == HEIGHT) exit(0);
}

int main() {
    setup();
    while (1) {
        draw();
        input();
        logic();
        Sleep(100);
    }
    return 0;
