#define WIDTH 30
#define HEIGHT 15
#define BRICK_ROWS 5
#define BRICK_COLS 10

int ballX, ballY, ballDX, ballDY, paddleX, paddleWidth;
int bricks[BRICK_ROWS][BRICK_COLS];

void setup() {
    ballX = WIDTH / 2;
    ballY = HEIGHT / 2;
    ballDX = 1;
    ballDY = 1;
    paddleX = WIDTH / 2 - 2;
    paddleWidth = 5;
    for (int i = 0; i < BRICK_ROWS; i++) {
        for (int j = 0; j < BRICK_COLS; j++) {
            bricks[i][j] = 1;
        }
    }
}

void draw() {
    system("cls");
    for (int y = 0; y < HEIGHT; y++) {
        for (int x = 0; x < WIDTH; x++) {
            if (y == ballY && x == ballX) printf("O");
            else if (y == HEIGHT - 1 && x >= paddleX && x < paddleX + paddleWidth) printf("#");
            else if (y < BRICK_ROWS && bricks[y][x]) printf("#");
            else printf(" ");
        }
        printf("\n");
    }
}

void input() {
    if (_kbhit()) {
        switch (_getch()) {
            case 'a': if (paddleX > 0) paddleX--; break;
