#include <stdio.h>
#include <stdlib.h>
#include <conio.h>
#include <windows.h>

#define WIDTH 30
#define HEIGHT 15

int ballX, ballY, ballDX, ballDY, paddle1Y, paddle2Y;
int player1Score, player2Score;

void setup() {
    ballX = WIDTH / 2;
    ballY = HEIGHT / 2;
    ballDX = 1;
    ballDY = 1;
    paddle1Y = HEIGHT / 2 - 2;
    paddle2Y = HEIGHT / 2 - 2;
    player1Score = 0;
    player2Score = 0;
}

void draw() {
    system("cls");
    for (int i = 0; i < WIDTH + 2; i++) printf("#");
    printf("\n");

    for (int y = 0; y < HEIGHT; y++) {
        for (int x = 0; x < WIDTH; x++) {
            if (x == ballX && y == ballY) printf("O");
            else if (x == 0 && y >= paddle1Y && y < paddle1Y + 3) printf("|");
            else if (x == WIDTH - 1 && y >= paddle2Y && y < paddle2Y + 3) printf("|");
            else printf(" ");
        }
        printf("\n");
    }

    for (int i = 0; i < WIDTH + 2; i++) printf("#");
    printf("\n");
    printf("Player 1: %d  Player 2: %d\n", player1Score, player2Score);
}

void input() {
    if (_kbhit()) {
        switch (_getch()) {
            case 'w': if (paddle1Y > 0) paddle1Y--; break;
            case 's': if (paddle1Y < HEIGHT - 3) paddle1Y++; break;
            case 'i': if (paddle2Y > 0) paddle2Y--; break;
            case 'k': if (paddle2Y < HEIGHT - 3) paddle2Y++; break;
        }
    }
}

void logic() {
    ballX += ballDX;
    ballY += ballDY;

    if (ballY == 0 || ballY == HEIGHT - 1) ballDY = -ballDY;
    if (ballX == 0) {
        if (ballY >= paddle1Y && ballY < paddle1Y + 3) ballDX = -ballDX;
        else { player2Score++; ballX = WIDTH / 2; ballY = HEIGHT / 2; }
    }
    if (ballX == WIDTH - 1) {
        if (ballY >= paddle2Y && ballY < paddle2Y + 3) ballDX = -ballDX;
        else { player1Score++; ballX = WIDTH / 2; ballY = HEIGHT / 2; }
    }
}

int main() {
    setup();
    while (1) {
        draw();
        input();
        logic();
        Sleep(10);
    }
    return 0;
}

#include <stdio.h>
#include <stdlib.h>

char board[3][3];
int row, col;
char player = 'X';

void setup() {
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            board[i][j] = ' ';
        }
    }
}

void draw() {
    system("cls");
    printf("Tic-Tac-Toe Game\n");
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            printf("|%c", board[i][j]);
        }
        printf("|\n");
    }
    printf("\n");
}

int checkWin() {
    for (int i = 0; i < 3; i++) {
        if (board[i][0] == board[i][1] && board[i][1] == board[i][2] && board[i][0] != ' ') return 1;
        if (board[0][i] == board[1][i] && board[1][i] == board[2][i] && board[0][i] != ' ') return 1;
    }
    if (board[0][0] == board[1][1] && board[1][1] == board[2][2] && board[0][0] != ' ') return 1;
    if (board[0][2] == board[1][1] && board[1][1] == board[2][0] && board[0][2] != ' ') return 1;
    return 0;
}

void input() {
    printf("Player %c, enter row and column (0-2): ", player);
    scanf("%d %d", &row, &col);
    if (board[row][col] == ' ') {
        board[row][col] = player;
    } else {
        printf("Invalid move! Try again.\n");
        input();
    }
}

void switchPlayer() {
    if (player == 'X') player = 'O';
    else player = 'X';
}

int main() {
    setup();
    while (1) {
        draw();
        input();
        if (checkWin()) {
            draw();
            printf("Player %c wins!\n", player);
            break;
        }
        switchPlayer();
    }
    return 0;
}

#include <stdio.h>
#include <string.h>
#include <stdlib.h>

char word[] = "programming";
char guessed[50];
int tries = 6;

void displayWord() {
    for (int i = 0; i < strlen(word); i++) {
        if (guessed[i] != 0) printf("%c ", guessed[i]);
        else printf("_ ");
    }
    printf("\n");
}

int checkGuess(char guess) {
    int correct = 0;
    for (int i = 0; i < strlen(word); i++) {
        if (word[i] == guess) {
            guessed[i] = guess;
            correct = 1;
        }
    }
    return correct;
}

int main() {
    for (int i = 0; i < strlen(word); i++) guessed[i] = 0;
    while (tries > 0) {
        printf("Word: ");
        displayWord();
        printf("Tries left: %d\n", tries);
        printf("Guess a letter: ");
        char guess = getchar();
        getchar();

        if (!checkGuess(guess)) tries--;
        int complete = 1;
        for (int i = 0; i < strlen(word); i++) {
            if (guessed[i] == 0) complete = 0;
        }
        if (complete) {
            printf("Congratulations! You guessed the word!\n");
            break;
        }
    }
    if (tries == 0) printf("Game Over! The word was: %s\n", word);
    return 0;
}

#include <stdio.h>
#include <stdlib.h>
#include <time.h>

#define SIZE 4

char board[SIZE][SIZE], revealed[SIZE][SIZE];

void setup() {
    char symbols[] = {'A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'};
    int k = 0;
    for (int i = 0; i < SIZE; i++) {
        for (int j = 0; j < SIZE; j++) {
            board[i][j] = symbols[k / 2];
            revealed[i][j] = 0;
            k++;
        }
    }
}

void draw() {
    system("cls");
    for (int i = 0; i < SIZE; i++) {
        for (int j = 0; j < SIZE; j++) {
            if (revealed[i][j]) printf("%c ", board[i][j]);
            else printf("* ");
        }
        printf("\n");
    }
}

int main() {
    srand(time(NULL));
    setup();
    int x1, y1, x2, y2;
    int matches = 0;

    while (matches < SIZE * SIZE / 2) {
        draw();
        printf("Enter the coordinates of the first card (row col): ");
        scanf("%d %d", &x1, &y1);
        revealed[x1][y1] = 1;
        draw();

        printf("Enter the coordinates of the second card (row col): ");
        scanf("%d %d", &x2, &y2);
        revealed[x2][y2] = 1;
        draw();

        if (board[x1][y1] == board[x2][y2]) {
            printf("It's a match!\n");
            matches++;
        } else {
            printf("Not a match. Try again.\n");
            revealed[x1][y1] = revealed[x2][y2] = 0;
        }
        getchar();
        getchar();
    }
    printf("Congratulations, you won!\n");
    return 0;
}
